/* eslint-disable */
import React, { useState, useRef,useEffect, useCallback } from "react";
import JoditEditor from "jodit-react";
import {Input,Divider} from 'antd';
import ReactDOM from "react-dom";
//import {debounce} from "./baidu";

function JoditDemo(){
    return <div>
        <label>Hello, this is Jodit!</label>
        <JoditApp />
    </div>;
}
const { TextArea } = Input;

function JoditApp() {
    const editor = useRef(null);
    const [content, setContent] = useState("");
    const config = {
        readonly: false,
        height: 400
    };
    const inputTmp = useState('');
    //load only once.
    useEffect(() => {
        inputTmp[1](content);
    },[]);

    const useUpdate = () => {
        const [_, setState] = useState(0);
        return useCallback(() => {
            setState((num: number): number => num + 1);
        }, []);
    };


    function onChangeHandler(content){
        //setContent(content);
    }

    function clickHandlerSerialize(event){
        inputTmp[1](content);
        console.log("serialize")
        console.log(content);
        console.log(inputTmp[0]);
    }

    function clickHandlerDeserialize(event){
        setContent(inputTmp[0]);
        console.log("deserialized ");
        console.log(content);
    }

    function onBlur(content){
        console.log("this is onBlur")
        setContent(content);
    }

    function inputEventHandler(event){
        inputTmp[1](event.target.value);
        console.log("input handler in textarea");
        console.log(inputTmp[0]);
    }

    return (
        <div >
            <h1>React Editors</h1>
            <h2>Start editing to see some magic happen!</h2>
            <JoditEditor
                ref={editor}
                value={content}
                config={config}
                onBlur={newContent => onBlur(newContent)}
                onChange={(newContent) => {onChangeHandler(newContent)}}
            />
            <button onClick={event=>clickHandlerSerialize(event)}>serialize</button>
            <label>    </label>
            <button onClick={event=>clickHandlerDeserialize(event)}>deserialize</button>
            <br></br>
            <TextArea
                state = {inputTmp[0]}
                value = {inputTmp[0]}
                onChange={(value)=>inputEventHandler(value)}
                style={{height: 'calc(22vh)', minHeight: 'calc(19.8vh)',maxHeight:'calc(50vh)',width:'calc(80vh)'}}
            />
        </div>
    );
}

ReactDOM.render(
    <JoditDemo />,
    document.getElementById('root')
);
