/* eslint-disable */
import React, { useState, useRef } from "react";
import JoditEditor from "jodit-react";
import {Input,Divider} from 'antd';

export function JoditDemo(){
    return <div>
        <label>Hello, this is Jodit!</label>
        <JoditApp />
        <textarea />
    </div>;
}

export function JoditApp() {
    const editor = useRef(null);
    const [content, setContent] = useState("<p>Start writ<strong>ing. Hello~</strong>~~~~</p><p><br></p>\n<table style=\"width: 100%;\"><tbody>\n<tr>\n\t<td>ok</td>\n\t<td>nia</td>\n\t<td>Jones</td></tr>\n<tr>\n\t<td>hello</td>\n\t<td>fine</td>\n\t<td>great</td></tr>\n<tr>\n\t<td><br></td>\n\t<td><br></td>\n\t<td><br></td></tr></tbody></table>");
    const config = {
        readonly: false,
        height: 400
    };

    function onChangeHandler(content){
        const JSONcontent = JSON.stringify(content)
        console.log("content:");
        console.log(content);
        console.log("JSONcontent:");
        console.log(JSONcontent);
    }

    function log(content){
        console.log("this is log content:");
        console.log(content);
    }

    return (
        <div >
            <h1>React Editors</h1>
            <h2>Start editing to see some magic happen!</h2>
            <JoditEditor
                ref={editor}
                value={content}
                config={config}
                onBlur={newContent => setContent(newContent)}
                onChange={(newContent) => {onChangeHandler(newContent)}}
            />
            <button ref={editor} value={content} onClick={content=>log(content)}>serialize</button><br></br>
            <textarea/>
        </div>
    );
}
